  // Normalize language name for lookup
  function normalizeLanguage(lang) {
    if (!lang) return "default";

    const normalized = lang.toLowerCase().trim();

    // Map common language variations
    const languageMap = {
      js: "javascript",
      ts: "typescript",
      py: "python",
      html5: "html",
      css3: "css",
      jsx: "javascript",
      tsx: "typescript",
      java: "java",
      zig: "zig",
    };

    return languageMap[normalized] || normalized;
  }

  // Get the appropriate icon for the language
  function getLanguageIcon(lang) {
    const normalizedLang = normalizeLanguage(lang);
    return languageIcons[normalizedLang] || languageIcons.default;
  }

  // The icon to display
  const icon = getLanguageIcon(language);
</script>

<div class="language-icon w-10 h-10 flex items-center justify-center">
  {@html icon}
</div>
